// SPDX-License-Identifier: MIT
pragma solidity >=0.8.9 <0.9.0;
// import "./Users.sol";

// TODO add NatSpec for every parameters like below
/// @title
/// @author Sarem Eskandary
/// @notice 
/// @dev
contract Certifiction {
    ////////////////////   State variables   ////////////////////
    address public owner;
    uint public schoolCount;
    uint public studentCount;
    ////////////////////   events     ////////////////////
    // event personAdded(User user);
    event studentCreated(address sender, bool created );
    event schoolCreated(address sender, bool created );
    event certificateCreated(address sender, bool created );
    event courseCreated(address sender, bool created );

    ////////////////////   enums     ////////////////////

    ////////////////////   struct     ////////////////////
    struct School {
        bytes32[]   certificateList;
        uint        certificatePointer;
        string      name;
        string      IPFShash; // additional info
        address     schoolOwner;
    }
    mapping (address=>School) schoolStruct;
    address[] public schoolList;

    struct Student {
        bytes32[]   certificateList;
        uint        certificatePointer;
        string      name;
        string      IPFShash; // additional info
    }
    mapping (address=>Student) studentListtruct;
    address[] public studentList;

    struct Certificate {
        uint        certificatePointer;
        address     studentID;
        address     schoolID;
        string      courseName;
    }
    mapping (bytes32=>Certificate) certificateStruct;
    bytes32[] public certificateList;

    ////////////////////   modifiers  ////////////////////
    modifier isOwner() {
        require(msg.sender == owner, "Must be the owner to call this function");
        _;
    }
    modifier verifyCaller(address _address) {
        require(msg.sender == _address, "Unrecognized caller");
        _;
    }
    modifier isSchool(address _schoolOwner) {
        require(msg.sender == schoolStruct[_schoolOwner].schoolOwner , "Must be the school to call this function");
        _;
    }

    /// @dev student should allow that how can see it's name or certificate
    // modifier isPersonAllowed() {
    //     require(personAllowed == true);
    //     _;
    // }

    constructor() {
    }

    /// @dev school should add student to the school list
    function createStudent(address _id,string memory _name, string memory _IPFShash) public returns(bool success) {
        studentListtruct[_id] = Student({
            certificateList: certificateList,
            certificatePointer: certificateList.length - 1,
            name: _name,
            IPFShash: _IPFShash
        });
        // emit studentCreated(msg.sender);
        return (true);
    }
    function createSchool(address _id, string memory _name, string memory _IPFShash) public returns (bool success) {
        schoolList.push(_id);
        schoolStruct[_id].certificatePointer = schoolList.length - 1;
        schoolStruct[_id].name = _name;
        schoolStruct[_id].IPFShash = _IPFShash;
        schoolStruct[_id].schoolOwner = msg.sender;
        return (true);
    }
    function createCourse(string memory _name, address _schoolID) 
    public isSchool(_schoolID) returns (bool success) {
        
    }
    function createCertificate(address _studentID, address _schoolID, string memory _courseName) 
    public isSchool(_schoolID) returns (bool success) {
        
    }

    /// @dev school should add person to the school list
    // function addstudentListPassedLesson(type name) public onlySchool {
    //     string lessonName;
    //     uint lessonGrade;
    // }

    // function create(string memory _courseName, address user) public onlySchool {
    //     items[_id].user == User;

    // }
    // function getCertificate(address copmany) view public onlySchool returns (string _copmany)  {
        
    // }

    // function fetchStudent(address _studentAddress) view public returns (type name) {
        
    // }
}
